mapper.select:
select a.id, u1.login_act as owner, a.name, a.start_date, a.end_date, a.cost, a.description, a.create_time, u2.login_act as create_by, a.edit_time, u3.login_act as edit_by from t_activity a left join t_user u1 on a.owner = u1.id left join t_user u2 on a.create_by = u2.id left join t_user u3 on a.edit_by = u3.id
and a.name like '%' #{activityName} '%'
and u1.login_act like '%' #{activityOwner} '%'
and a.start_date >= #{activityStartDate}
and a.end_date <= #{activityEndDate}
order by a.create_time desc limit #{pageShowNumber}, 10

select count(*) from t_activity a left join t_user u1 on a.owner = u1.id left join t_user u2 on a.create_by = u2.id left join t_user u3 on a.edit_by = u3.id
and a.name like '%' #{activityName} '%'
and u1.login_act like '%' #{activityOwner} '%'
and a.start_date >= #{activityStartDate}
and a.end_date <= #{activityEndDate}
